    <style>
      * {
        transform-box: fill-box;
        transform-origin: center;
        fill-rule: nonzero;
      }

        main {
          width: 100%;
          height: 100vh;
          display: grid;
          place-items: center;
        }

          svg {
            max-width: 600px;
          }

            #circle-semi-line {
              transform-origin: center bottom;
              animation: animeCircleSemiLine 4s linear infinite
            }

            #chat-bubble-1 {
              opacity: 0;
              transform-origin: right bottom;
              animation: animeChatBubble 8s linear 0s infinite
            }

            #chat-bubble-2 {
              opacity: 0;
              transform-origin: left bottom;
              animation: animeChatBubble 8s linear 1s infinite
            }

            #chat-bubble-3 {
              opacity: 0;
              transform-origin: left bottom;
              animation: animeChatBubble 8s linear 3s infinite
            }

      @keyframes animeCircleSemiLine {
        0% { transform: rotate(0); }
        100% { transform: rotate(360deg); }
      }

      @keyframes animeChatBubble {
        0% { transform: scale(0); opacity: 0; }
        3% { transform: scale(1.1); opacity: 1; }
        6% { transform: scale(1); opacity: 1; }
        44% { transform: scale(1); opacity: 1; }
        47% { transform: scale(1.1); opacity: 1; }
        50% { transform: scale(0); opacity: 0; }
        100% { transform: scale(0); opacity: 0; }
      }
    </style>
